import React, { useState, useEffect } from "react";
import "./game-board.css";

const ROWS = 6;
const COLS = 7;

function GameBoard({ opponent }) {
  const [board, setBoard] = useState(Array(ROWS).fill().map(() => Array(COLS).fill(null)));
  const [currentPlayer, setCurrentPlayer] = useState('游댮');
  const [gameOver, setGameOver] = useState(false);
  const [highlightedCol, setHighlightedCol] = useState(null);

  const resetGame = () => {
    setBoard(Array(ROWS).fill().map(() => Array(COLS).fill(null)));
    setCurrentPlayer('游댮');
    setGameOver(false);
    setHighlightedCol(null);
  };

  const handleColumnClick = (colIndex) => {
    if (gameOver) return;

    for (let row = ROWS - 1; row >= 0; row--) {
      if (!board[row][colIndex]) {
        const newBoard = board.map((r) => [...r]);
        newBoard[row][colIndex] = currentPlayer;
        setBoard(newBoard);
        setCurrentPlayer(currentPlayer === '游댮' ? '游리' : '游댮');
        checkForWinner(newBoard, row, colIndex);
        break;
      }
    }
  };

  const checkForWinner = (newBoard, row, col) => {
    if (
      checkDirection(newBoard, row, col, 0, 1) ||
      checkDirection(newBoard, row, col, 0, -1) ||
      checkDirection(newBoard, row, col, 1, 0) ||
      checkDirection(newBoard, row, col, 1, 1) ||
      checkDirection(newBoard, row, col, 1, -1)
    ) {
      setGameOver(true);
      alert(`${currentPlayer} venceu!`);
    }
  };

  const checkDirection = (board, row, col, rowDir, colDir) => {
    let count = 1;
    for (let i = 1; i < 4; i++) {
      const r = row + i * rowDir;
      const c = col + i * colDir;
      if (r >= 0 && r < ROWS && c >= 0 && c < COLS && board[r][c] === currentPlayer) {
        count++;
      } else {
        break;
      }
    }
    for (let i = 1; i < 4; i++) {
      const r = row - i * rowDir;
      const c = col - i * colDir;
      if (r >= 0 && r < ROWS && c >= 0 && c < COLS && board[r][c] === currentPlayer) {
        count++;
      } else {
        break;
      }
    }
    return count >= 4;
  };

  const botPlay = () => {
    if (gameOver || currentPlayer === '游댮') return;

    let colIndex = -1;
    for (let col = 0; col < COLS; col++) {
      for (let row = ROWS - 1; row >= 0; row--) {
        if (!board[row][col]) {
          colIndex = col;
          break;
        }
      }
      if (colIndex !== -1) break;
    }

    if (colIndex !== -1) {
      handleColumnClick(colIndex);
    }
  };

  useEffect(() => {
    if (opponent === '2' && currentPlayer === '游리') {
      const timeout = setTimeout(() => {
        botPlay();
      }, 500);
      return () => clearTimeout(timeout);
    }
  }, [currentPlayer, opponent, board]);

  const handleMouseMove = (e) => {
    const boardRect = e.target.getBoundingClientRect();
    const x = e.clientX - boardRect.left;
    const colIndex = Math.floor(x / (boardRect.width / COLS));
    setHighlightedCol(colIndex);
  };

  return (
    <div>
      {/* Pe칞a arrast치vel */}
      <div
        className="draggable-piece"
        draggable
        onDragStart={(e) => e.dataTransfer.setData("col", "drag")}
        style={{
          width: 60,
          height: 60,
          backgroundColor: currentPlayer === '游댮' ? 'red' : 'yellow',
          borderRadius: '50%',
          border: '2px solid white',
          margin: '0 auto',
          marginBottom: 10,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontSize: '30px',
          cursor: gameOver ? 'not-allowed' : 'grab',
          opacity: gameOver ? 0.4 : 1
        }}
      >
        {currentPlayer}
      </div>

      <div
        className="game-board"
        onMouseMove={handleMouseMove}
        style={{ position: 'relative' }}
      >
        {board.map((row, rowIndex) => (
          <div key={rowIndex} className="board-row" style={{ display: 'flex' }}>
            {row.map((cell, colIndex) => (
              <div
                key={colIndex}
                onClick={() => handleColumnClick(colIndex)}
                onDragOver={(e) => e.preventDefault()}
                onDrop={() => handleColumnClick(colIndex)}
                className={`board-cell ${highlightedCol === colIndex ? 'highlighted' : ''}`}
                style={{
                  width: 60,
                  height: 60,
                  backgroundColor: '#1e3a8a',
                  border: '2px solid white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '30px',
                  cursor: gameOver ? 'not-allowed' : 'pointer',
                }}
              >
                {cell}
              </div>
            ))}
          </div>
        ))}

        {/* Pe칞a flutuante para indica칞칚o visual da coluna */}
        {highlightedCol !== null && (
          <div
            className="highlight-piece"
            style={{
              position: 'absolute',
              top: -60,
              left: highlightedCol * 60,
              width: 60,
              height: 60,
              backgroundColor: currentPlayer === '游댮' ? 'red' : 'yellow',
              borderRadius: '50%',
              border: '2px solid white',
            }}
          ></div>
        )}
      </div>

      {gameOver && (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <button onClick={resetGame}>Reiniciar Jogo</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
