import React, { useState } from "react";
import GameSetup from "./components/Game-Setup/GameSetup.component";
import GameBoard from "./components/gameboard/GameBoard.component";
import "./assets/styles/App.css";

function App() {
  const [config, setConfig] = useState(null);

  return (
    <>
      <div className="header-container">
        <h2 className="header-left">4 em Linha</h2>
        <h2 className="header-right">4 em Linha</h2>
      </div>
      <div id="tit-container">
        {!config ? (
          <GameSetup onStartGame={setConfig} />
        ) : (
          <GameBoard {...config} />
        )}
      </div>
    </>
  );
}

export default App;
