import React, { useState, useEffect, useRef } from "react";
import WinnerModal from "../Confetis/WinnerModal.component";
import "./game-board.css";

const ROWS = 6;
const COLS = 7;

function GameBoard({
  opponent,
  player1Name,
  player2Name,
  player1Color,
  player2Color,
}) {
  // ---- 1) Gera√ß√£o das c√©lulas especiais ----
  const generateSpecialCells = (count = 5) => {
    const setCoords = new Set();
    while (setCoords.size < count) {
      const r = Math.floor(Math.random() * ROWS);
      const c = Math.floor(Math.random() * COLS);
      setCoords.add(`${r}-${c}`);
    }
    return Array.from(setCoords).map((s) => {
      const [row, col] = s.split("-").map(Number);
      return { row, col };
    });
  };

  const getRandomStartingPlayer = () =>
    Math.random() < 0.5
      ? { name: player1Name, color: player1Color }
      : { name: player2Name, color: player2Color };

  // ---- 2) Estados principais ----
  const [specialCells, setSpecialCells] = useState(() => generateSpecialCells());
  const [board, setBoard] = useState(
    Array(ROWS).fill().map(() => Array(COLS).fill(null))
  );
  const [currentPlayer, setCurrentPlayer] = useState(getRandomStartingPlayer());
  const [gameOver, setGameOver] = useState(false);
  const [highlightedCol, setHighlightedCol] = useState(null);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [winner, setWinner] = useState(null);

  // ---- 3) Refer√™ncia para o intervalo do timer ----
  const timerRef = useRef(null);

  // ---- 4) useEffect para rodar o timer por turno ----
  useEffect(() => {
    // Limpa qualquer intervalo pendente
    clearInterval(timerRef.current);
    // Zera o cron√¥metro no in√≠cio de cada turno ou reset
    setTimeElapsed(0);

    // Se o jogo n√£o acabou, dispara novo timer
    if (!gameOver) {
      timerRef.current = setInterval(() => {
        setTimeElapsed((prev) => prev + 1);
      }, 1000);
    }

    // Cleanup quando mudar de turno, gameOver ou desmontar
    return () => clearInterval(timerRef.current);
  }, [currentPlayer, gameOver]);

  // ---- 5) Fun√ß√£o para resetar tudo ----
  const resetGame = () => {
    setBoard(Array(ROWS).fill().map(() => Array(COLS).fill(null)));
    setCurrentPlayer(getRandomStartingPlayer());
    setGameOver(false);
    setHighlightedCol(null);
    setTimeElapsed(0);
    setWinner(null);
    setSpecialCells(generateSpecialCells());
    // O useEffect acima j√° vai cuidar de reiniciar o timer
  };

  // ---- 6) Handler de clique na coluna ----
  const handleColumnClick = (colIndex) => {
    if (gameOver) return;

    for (let row = ROWS - 1; row >= 0; row--) {
      if (!board[row][colIndex]) {
        const newBoard = board.map((r) => [...r]);
        newBoard[row][colIndex] = currentPlayer.color;
        setBoard(newBoard);

        checkForWinner(newBoard, row, colIndex);

        const isSpecial = specialCells.some(
          (cell) => cell.row === row && cell.col === colIndex
        );

        if (!isSpecial) {
          setCurrentPlayer((p) =>
            p.color === player1Color
              ? { name: player2Name, color: player2Color }
              : { name: player1Name, color: player1Color }
          );
        } else {
          console.log("‚≠ê B√¥nus ativado! Jogue de novo.");
          // currentPlayer n√£o muda; effect s√≥ reinicia timer
        }

        // <-- removido o setTimeElapsed(0) daqui, pois o useEffect j√° faz o reset
        break;
      }
    }
  };

  // ---- 7) Detec√ß√£o de vencedor ----
  const checkForWinner = (newBoard, row, col) => {
    const dirs = [
      [0, 1],
      [1, 0],
      [1, 1],
      [1, -1],
    ];
    if (dirs.some(([dx, dy]) => checkDirection(newBoard, row, col, dx, dy))) {
      setGameOver(true);
      setWinner(currentPlayer.name);
    }
  };

  const checkDirection = (board, row, col, dx, dy) => {
    let count = 1;
    for (let dir of [1, -1]) {
      for (let i = 1; i < 4; i++) {
        const r = row + i * dx * dir;
        const c = col + i * dy * dir;
        if (
          r >= 0 &&
          r < ROWS &&
          c >= 0 &&
          c < COLS &&
          board[r][c] === currentPlayer.color
        ) {
          count++;
        } else break;
      }
    }
    return count >= 4;
  };

  // ---- 8) Highlight de coluna e estilo ----
  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const colWidth = rect.width / COLS;
    setHighlightedCol(Math.floor(x / colWidth));
  };

  const getColorStyle = (c) => {
    switch (c) {
      case "üî¥":
        return "red";
      case "üü°":
        return "yellow";
      case "üîµ":
        return "blue";
      default:
        return c || "gray";
    }
  };

  // ---- 9) Render ----
  return (
    <div>
      {winner && (
        <WinnerModal winner={winner} onClose={() => setWinner(null)} />
      )}

      <div className="game-info">
        {gameOver
          ? `Jogo encerrado - Vencedor: ${winner}`
          : `${currentPlayer.name} (${currentPlayer.color}) - Tempo: ${timeElapsed}s`}
      </div>

      <div className="game-board" onMouseMove={handleMouseMove}>
        {board.map((rowArr, r) => (
          <div key={r} className="board-row">
            {rowArr.map((cell, c) => {
              const isSpecial = specialCells.some(
                (cell) => cell.row === r && cell.col === c
              );
              return (
                <div
                  key={c}
                  className={[
                    "board-cell",
                    highlightedCol === c && "highlighted",
                    gameOver && "disabled",
                    isSpecial && "special",
                  ]
                    .filter(Boolean)
                    .join(" ")}
                  onClick={() => handleColumnClick(c)}
                  onDragOver={(e) => e.preventDefault()}
                  onDrop={() => handleColumnClick(c)}
                  style={{ color: cell ? getColorStyle(cell) : undefined }}
                >
                  {cell}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      {gameOver && (
        <div className="game-over-button">
          <button onClick={resetGame}>Reiniciar Jogo</button>
          <button
            className="back-button"
            onClick={() => (window.location.href = "/")}
          >
            üè† P√°gina Inicial
          </button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
